parameters.bodyParametersJson:
={
  "model": "gpt-4",
  "messages": [
    {
      "role": "system",
      "content": "You are a writing style analyst. Analyze the following article and return a detailed, structured breakdown of the writing style in JSON format. Focus on tone, sentence length, vocabulary, narrative voice, pacing, syntax patterns, common rhetorical devices, and typical story structure."
    },
    {
      "role": "user",
      "content": {{$json["content"]}}
    }
  ]
}

parameters.functionCode:
return [
  {
    json: {
      style_guide: {
        overall_tone: "Critical, informative, and politically aware",
        sentence_and_paragraph_guidelines: {
          length: "12–20 words per sentence, 2–3 sentence paragraphs",
          structure: "Mix of compound and complex structures, variation to create rhythm"
        },
        story_structure: [
          "1. Introduce the issue with a historical or emotional hook",
          "2. Present a current example or case study",
          "3. Analyze implications using quotes and background",
          "4. End with reflection or future outlook"
        ],
        signature_patterns: [
          "Frequent use of passive voice for neutrality",
          "Regular inclusion of public sentiment or protest language",
          "Dense informational blocks followed by shorter emotional lines"
        ],
        tone_handling: "Avoid sensationalism; stay fact-based but not emotionally cold"
      }
    }
  }
];
